import Head from "next/head";
import Flexbox from 'flexbox-react';

const encode = data => {
  return Object.keys(data)
    .map(key => encodeURIComponent(key) + "=" + encodeURIComponent(data[key]))
    .join("&");
};

function Home() {
  const handleSubmit = e => {
    if (email == null || email.length <= 0) {
      return;
    }

    fetch("/", {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: encode({ "form-name": "contact", email: email })
    })
      .then(() => setShowConfirmation(true))
      .catch(error => console.log(error));

    e.preventDefault();
  };
  return (
    <>
      <Head>
        <meta charSet="utf-8" />
        <meta viewport-fit="cover" />
        <title>Orgynize</title>
        <link rel="canonical" href="https://orgynize.app" />
        <link
          href="https://fonts.googleapis.com/css?family=Abel|Sedgwick+Ave+Display&display=swap"
          rel="stylesheet"
        />
      </Head>
      <style global jsx>{`
        html,
        body {
          background: black;
          color: white;
          font-family: "Abel";
          margin: 0;
          padding: 0;
          height: 100vh;
        }
      `}</style>
      <style jsx>{`
        h1 {
          font-family: "Sedgwick Ave Display";
          text-align: center;
        }

        .footer {
          border-top: 0.5px solid white;
        }ÃŸ
      `}</style>
      <Flexbox flexDirection="column" maxHeight="100vh" justifyContent="center" alignItems="center">
        <Flexbox flexGrow={1}>
            <h1>For party planners that are not freaks in the spreadsheets</h1>
        </Flexbox>
        <Flexbox flexGrow={3}>
          <img className="logo" src="/logo.png" />
        </Flexbox>
        <Flexbox>
        <form
          name="contact"
          method="post"
          data-netlify="true"
          data-netlify-honeypot="bot-field"
          onSubmit={handleSubmit}
        >
          <input type="hidden" name="form-name" value="contact" />
          <label>
            Want to be notified when we're ready for you to start partying?
          </label>
          <input name="email" placeholder="Your email here" type="text" />
          <button>Go</button>
        </form>
        </Flexbox>
        <Flexbox>
          <p>Orgynize by Quxxxn</p>
        </Flexbox>
      </Flexbox>
    </>
  );
}

export default Home;
